var reg_h = flow.get('reg_h') || '';
var reg_c = flow.get('reg_c') || '';
var reg_count = flow.get('reg_count') || 0;

var rif = 'common';
var prefix = 'SX1302_REG_';
var base_addr = prefix + rif.toUpperCase() + '_BASE_ADDR';

// register definition
var page = '';
var addr = '';
var offs = '';
var sign = '';
var leng = '';
var rdon = '';
var dflt = '';
var reg_comment = '';

for(var register in msg.payload)
{
    //console.log(register);
    for(var field in msg.payload[register].fields)
    {
        //console.log(' ' + field);
        page = '0';
        addr = base_addr + '+' + msg.payload[register].addr;
        offs = msg.payload[register].fields[field].pos;
        sign = '0';
        leng = msg.payload[register].fields[field].width;
        rdon = (msg.payload[register].fields[field].readonly === false) ? '0' : '1';
        dflt = msg.payload[register].fields[field].value;
        reg_comment = (rif + '_' + register + '_' + field).toUpperCase();

        reg_h += '#define ' + prefix + reg_comment + ' ' + reg_count + '\n';
        reg_c += '    '; // indent
        reg_c += '{' + page + ',' + addr + ',' + offs + ',0,' + leng + ',' + rdon + ',' + dflt + '}, // ' + reg_comment + '\n';

        reg_count += 1;
    }
}

console.log(reg_h);
console.log(reg_c);

flow.set('reg_count', reg_count);
flow.set('reg_h', reg_h);
flow.set('reg_c', reg_c);

return msg;

